    @Override
    public TestEntry getTestEntry() {
        return TestEntry.getInstance(new Solution0030(), "findSubstring", String.class, String[].class);
    }

    public class Solution0030 {
        public List<Integer> findSubstring(String s, String[] words) {
            Set<Integer> ansSet = new HashSet<>();

            if(s.length()==0){
                return new ArrayList<>();
            }

            int wordsNumber = words.length;
            if(wordsNumber==0){
                return new ArrayList<>();
            }
            int wordLength = words[0].length();
            int substringLength = wordsNumber*wordLength;
            if(substringLength>s.length()){
                return new ArrayList<>();
            }

            int[][] indexAA = new int[wordsNumber][];
            for (int i = 0; i < indexAA.length; i++) {
                final int[] t = MyStringTools.allIndexOfWithCover(s, words[i]);
                if(t.length==0){
                    return new ArrayList<>();
                }
                indexAA[i] = t;
            }

            int[] index = new int[wordsNumber];
            for (int i = 0; i < index.length; i++) {
                index[i] = 0;
            }

            do {
                int[] ansArr = new int[wordsNumber];
                for (int i = 0; i < ansArr.length; i++) {
                    ansArr[i] = indexAA[i][index[i]];
                }
                MyArrayTools.quickSort(ansArr);

                int ans = isAns(ansArr,wordLength);
                if(ans!=-1){
                    ansSet.add(ans);
                }
            } while (MyArrayTools.addOne(index, indexAA) == 0);


            return new ArrayList<>(ansSet);
        }

        private int isAns(int[] ans,int wordLength){
            for (int i = 1 ; i < ans.length; i++) {
                if(ans[i]-ans[i-1]!=wordLength){
                    return -1;
                }
            }

            return ans[0];
        }

    }
