    public class Solution0030 {
        //Runtime: 102 ms, faster than 34.15% of Java online submissions for Substring with Concatenation of All Words.
        //Memory Usage: 43.7 MB, less than 21.43% of Java online submissions for Substring with Concatenation of All Words.
        public List<Integer> findSubstring(String s, String[] words) {
            List<Integer> ansList = new ArrayList<>();
            int stringLength = s.length();
            if(stringLength==0){
                return ansList;
            }
            int wordNumber = words.length;
            if(wordNumber==0){
                return ansList;
            }
            int wordLength = words[0].length();
            if(stringLength<wordLength*wordNumber){
                return ansList;
            }


            Map<String,Integer> wordMap = new HashMap<>();
            int ascii = 0;
            for (String word : words) {
                wordMap.put(word,wordMap.getOrDefault(word,0)+1);
                for (int i = 0; i < word.length(); i++) {
                    ascii+=word.charAt(i);
                }
            }

            int asciiTest = 0;
            for (int i = 0 ; i < wordNumber * wordLength; i++) {
                asciiTest+=s.charAt(i);
            }


            for (int i = 0; i < stringLength - wordNumber * wordLength + 1; i++) {
                if(i>=1){
                    asciiTest-=s.charAt(i-1);
                    asciiTest+=s.charAt( wordNumber * wordLength + i - 1);
                }

                //ascii
                if(ascii==asciiTest){
                    //进一步判断
                    Map<String,Integer> wordMapTest = new HashMap<>();
                    int j = i;
                    for (; j < i + wordNumber * wordLength ; j+=wordLength) {
                        final String wordTest = s.substring(j, j + wordLength);
                        final Integer wordTimes = wordMap.getOrDefault(wordTest, 0);
                        if(wordTimes!=0){
                            final Integer appearTimes = wordMapTest.getOrDefault(wordTest, 0) + 1;
                            if(appearTimes>wordTimes){
                                break;
                            }
                            wordMapTest.put(wordTest, appearTimes);
                        }else {
                            break;
                        }
                    }

                    if(j==i + wordNumber * wordLength){
                        ansList.add(i);
                    }
                }
            }

            return ansList;
        }
    }


